import { fetchIngridients } from "./ingredientQuery";
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  ingridients: [],
  isLoding: false,
  error: " ",
};

const burgerIngridientsSlice = createSlice({
  name: "ingridients",
  initialState,

  extraReducers: (builder) => {
    builder
      // Add reducers for additional action types here, and handle loading state as needed
      .addCase(fetchIngridients.pending.type, (state) => {
        state.isLoding = false;
        state.error = " ";
      })

      .addCase(fetchIngridients.fulfilled.type, (state, action) => {
        state.isLoding = true;
        state.error = " ";
        state.ingridients = action.payload.data;
      })

      .addCase(fetchIngredients.rejected.type, (state, action) => {
        state.isLoding = false;
        state.error = action.payload;
      });
  },
});

export const {
  ingredientsUploading,
  ingredientsUpload,
  ingredientsUploadError,
} = burgerIngredientsSlice.actions;
export default burgerIngredientsSlice.reducer;
