import React from 'react';
import styles from './burger-ingridient-tab.module.css';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';

function BurgerIngridientTab() {

    const [current, setCurrent] = React.useState('one')

    return (
     <>
        <h1 className={styles.main_title + " text_type_main-large" + ' pt-10 pb-5'}>
        Соберите бургер
        </h1>

        <div style={{ display: 'flex' }} className={styles.section + 'pb-10'}>
        <Tab value="Булки" active={current === 'Булки'} onClick={setCurrent}>
        Булки
        </Tab>
        <Tab value="Соусы" active={current === 'Соусы'} onClick={setCurrent}>
        Соусы
        </Tab>
        <Tab value="Начинки" active={current === 'Начинки'} onClick={setCurrent}>
        Начинки
        </Tab>
        </div>
     </>
    )
}

export default BurgerIngridientTab
