import styles from "./app.module.css";
import { useState } from "react";
import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { ingredientPropType } from '../../utils/prop-types.js';

import AppHeader from "../header/AppHeader/AppHeader";
import { data } from "../../utils/data.js";
import BurgerIngridient from "../main/BurgerIngredients/BurgerIngredient/BurgerIngredient";

function App() {

  const [isModalOpen, setIsOpen] = useState(false);
  const [dataIngridients, setData] = useState([]);
  const [imageIngridient, setImageIngredient] = useState(null);
  const [isOpenOrderDetails, setOpenOrderDetails] = useState(false)
  const [isClickOrderList, setClickOrderList] = useState(false)
  const [isClickIngredient, setClickIngredient] = useState(false)
  
  const onClick = () => {
    setIsOpen(true)
  }

  const childForModal = () => {
    return (
      <Modal onClick={onClick} setIsOpen={setIsOpen}>
        {isClickOrderList && <OrderDetails/> || isClickIngredient && <Ingridient imageIngridient={imageIngridient}/>}
      </Modal>     
    )
  }

  const getData = () => {
    return (
      fetch("https://norma.nomoreparties.space/api/ingredients")
        .then((res) => {
          if (res.ok) {
            return res.json();
          }
          return Promise.reject(`Ошибка: ${res.status}`);
        })
        .then((res) => {
        setData(res.data)
      })
    )
  }

    useEffect(() => {
      
      getData()
    
    }, [])

  if (dataIngridients.length < 1) return null
  
    

}

export default App;
